const char* lua_default_video = "Window = { x = 640, y = 480, vsync = false, MSAA = false, fullscreen = false, borderless = false }\n";
const char* lua_ini_load = "local inifile = require(\'lua/packages/inifile\')\n\nfunction LoadSettings(a)\n    local conf = inifile.parse(a)\n    Window[\"x\"] = conf[\'Window\'][\'Width\']\n    Window[\"y\"] = conf[\'Window\'][\'Height\']\n    Window[\"vsync\"] = conf[\'Window\'][\'VSync\']\n    Window[\"MSAA\"] = conf[\'Window\'][\'MSAA\']\n    Window[\"fullscreen\"] = conf[\'Window\'][\'Fullscreen\']\n    Window[\"borderless\"] = conf[\'Window\'][\'Borderless\']\n    return true\nend";
const char* lua_ini_create = "local inifile = require(\'lua/packages/inifile\')\n\nfunction CreateSettings(a)\n    local conf = {\n        Window = {\n            Width = 640,\n            Height = 480,\n            VSync = false,\n            MSAA = false,\n            Fullscreen = false,\n            Borderless = false\n        }\n    }\n    inifile.save(a, conf)\nend";
